      surface: Color(0xFFFFFFFF),
      onSurface: Color(0xFF1A1918),
      surfaceVariant: Color(0xFFEDECEA),
      onSurfaceVariant: Color(0xFF6D6C6A),
      outline: Color(0xFFD1D0CD),
      outlineVariant: Color(0xFFE5E4E1),
      shadow: Color(0xFF000000),
