      primaryFixed: Color(0xFFC8F0D8),
      onPrimaryFixed: Color(0xFF1A1918),
      primaryFixedDim: Color(0xFFACF2C8),
      onPrimaryFixedVariant: Color(0xFF2D6B47),
      secondaryFixed: Color(0xFFEDECEA),
      onSecondaryFixed: Color(0xFF1A1918),
      secondaryFixedDim: Color(0xFF9C9B99),
      onSecondaryFixedVariant: Color(0xFF6D6C6A),
      tertiaryFixed: Color(0xFFFFF0E6),
